# Indirizzi delle factory per Uniswap e SushiSwap
uniswap_factory_address = '0x5C69bEe701ef814a2B6a3EDD4B1652CB9cc5aA6f' #V2
sushiswap_factory_address = '0xC0AEe478e3658e2610c5F7A4A2E1777cE9e4f2Ac' #V2

# Contratti della factory
uniswap_factory_contract = web3.eth.contract(address=uniswap_factory_address, abi=factory_abi)
sushiswap_factory_contract = web3.eth.contract(address=sushiswap_factory_address, abi=factory_abi)

# Mappatura simboli -> indirizzi
TOKEN_ADDRESSES = {
    'USDT': '0xdAC17F958D2ee523a2206206994597C13D831ec7',
    'DAI': '0x6B175474E89094C44Da98b954EedeAC495271d0F',
    'WETH': '0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2',
    # Aggiungi qui altre mappature
}

# Lista delle coppie da monitorare (simboli)
pairs = [
    ('USDT', 'DAI'),
    ('USDT', 'WETH'),
    # Aggiungi altre coppie qui
]

def get_decimals(token_address):
    token_contract = web3.eth.contract(address=token_address, abi=token_abi)
    decimals = token_contract.functions.decimals().call()
    return decimals

def get_price(factory_contract, token0_symbol, token1_symbol):
    token0_address = TOKEN_ADDRESSES[token0_symbol]
    token1_address = TOKEN_ADDRESSES[token1_symbol]
    pair_address = factory_contract.functions.getPair(token0_address, token1_address).call()
    pair_contract = web3.eth.contract(address=pair_address, abi=pair_abi)
    
    reserves = pair_contract.functions.getReserves().call()
    token0_address_from_pair = pair_contract.functions.token0().call()
    token1_address_from_pair = pair_contract.functions.token1().call()

    # Ottieni i decimali di ciascun token
    decimals_token0 = get_decimals(token0_address)
    decimals_token1 = get_decimals(token1_address)

    if token0_address_from_pair.lower() == token0_address.lower():
        reserve_token0 = reserves[0]
        reserve_token1 = reserves[1]
    else:
        reserve_token0 = reserves[1]
        reserve_token1 = reserves[0]

    # Calcola il prezzo tenendo conto dei decimali
    price = (reserve_token1 / (10 ** decimals_token1)) / (reserve_token0 / (10 ** decimals_token0))
    
    return price

def get_network_fee(pair_contract):
    gas_price = web3.eth.gas_price
    gas_estimate = web3.eth.estimate_gas({
        'to': pair_contract.address,
        'data': pair_contract.encodeABI(fn_name='getReserves')
    })
    fee_wei = gas_price * gas_estimate
    fee_eth = web3.from_wei(fee_wei, 'ether')
    
    return fee_eth

try:
    while True:
        for token0_symbol, token1_symbol in pairs:
            # Prezzi su Uniswap
            uniswap_price = get_price(uniswap_factory_contract, token0_symbol, token1_symbol)
            pair_address = uniswap_factory_contract.functions.getPair(TOKEN_ADDRESSES[token0_symbol], TOKEN_ADDRESSES[token1_symbol]).call()
            pair_contract = web3.eth.contract(address=pair_address, abi=pair_abi)
            uniswap_fee = get_network_fee(pair_contract)

            # Prezzi su SushiSwap
            sushiswap_price = get_price(sushiswap_factory_contract, token0_symbol, token1_symbol)
            sushiswap_pair_address = sushiswap_factory_contract.functions.getPair(TOKEN_ADDRESSES[token0_symbol], TOKEN_ADDRESSES[token1_symbol]).call()
            sushiswap_pair_contract = web3.eth.contract(address=sushiswap_pair_address, abi=pair_abi)
            sushiswap_fee = get_network_fee(sushiswap_pair_contract)

            # Stampa i risultati per ogni coppia
            print(f'Coppia {token0_symbol}/{token1_symbol}:')
            print(f'  Prezzo su Uniswap: {uniswap_price}')
            print(f'  Fee su Uniswap: {uniswap_fee} ETH')
            print(f'  Prezzo su SushiSwap: {sushiswap_price}')
            print(f'  Fee su SushiSwap: {sushiswap_fee} ETH')

        # Aspetta un secondo prima di ripetere il controllo
        time.sleep(1)
except KeyboardInterrupt:
    print("Interrotto dall'utente.")
